import java.util.Scanner;

public class BJ10950 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Scanner sc = new Scanner(System.in);
		int T = 0;
		int A = 0;
		int B = 0;
		T = sc.nextInt();
		for(int i = 0; i < T; i++) {
			A = sc.nextInt();
			B = sc.nextInt();
			
		}
		System.out.println(A + B);
	}

}
